update.foreach:
UPDATE t_task
updateDate = now(), updateBy = #{item.updateBy}, collectorId = #{item.collectorId}, collectorName = #{item.collectorName}, collectPeriodBegin = #{item.collectPeriodBegin}, collectPeriodEnd = #{item.collectPeriodEnd}, collectCycle = #{item.collectCycle}
WHERE id = #{item.id}

UPDATE t_task
updateDate = now(), updateBy = #{item.updateBy}, collectorId = #{item.collectorId}, collectorName = #{item.collectorName}, collectPeriodBegin = #{item.collectPeriodBegin}, collectPeriodEnd = #{item.collectPeriodEnd}, collectCycle = #{item.collectCycle}, collectTaskStatus = #{item.collectTaskStatus}
WHERE id = #{item.id}

mapper.select:
SELECT CASE WHEN o.status = 'payment' THEN DATEDIFF(curdate(), o.repayment_time) ELSE 0 END as "overduedays", private String customerId;#用户code private String customerName; //客户姓名 private String mobile; // 手机号 private String orderType;//订单类型 private String productType;//产品类型 private String orderStatus;//订单状态 private BigDecimal loanAmount;//借贷金额 private Integer loanTerm;//借贷期限 private BigDecimal lentAmountr;//放款金额 private String interestMode;//利息模式 private BigDecimal interestValue;//利息值 private String penaltyMode;//罚息模式 private BigDecimal penaltyValue;//罚息值 private BigDecimal reliefAmount;//减免金额 private BigDecimal chargeValue;//服务费值 private BigDecimal postponeUnitCharge;//延期单位服务费 private Integer postponeCount;//延期次数 private Date lendTime;//放款时间 private Date payoffTime;//还清时间 private Date repaymentTime;//到期还款日期 private String collectorId;//催讨人id private String collectorName;//催讨人名 private Date taskStartTime;//任务起始时间 private Date taskEndTime;//任务结束时间 private Integer collectPeriodBegin;//催讨周期-逾期周期起始 private Integer collectPeriodEnd;//催讨周期-逾期周期截至 private String collectTaskStatus;//催款任务状态(未开启任务，任务进行中，任务结束，延期) private String collectTelRemark;//催收备注 private Date collectTime;//操作时间 private String collectCycle;//催收队列 private String remark;//备注 private boolean ispayoff; // 任务所对应的订单是否还清 FROM t_task t
t.DunningCycle = #{collectCycle} AND t.collectTaskStatus = #{collectTaskStatus} AND DATEDIFF(curdate(), o.repayment_time) NOT BETWEEN #{begin} AND #{end}
ORDER BY corpusAmount DESC
