mapper.select:
SELECT u.name AS "name", t.mobile AS "mobile", CASE WHEN t.order_status = 'payoff' THEN 0 WHEN t.order_status = 'payment' and DATEDIFF(CURDATE(), t.repayment_time) > 0 THEN DATEDIFF(CURDATE(), t.repayment_time)*t.penalty_value + IFNULL(t.loan_amount,0)+IFNULL(t.interest_value,0) WHEN t.order_status = 'payment' and DATEDIFF(CURDATE(), t.repayment_time) <= 0 THEN IFNULL(t.loan_amount,0)+IFNULL(t.interest_value,0) END AS "repayAmount", IFNULL(t.loan_amount,0)+IFNULL(t.interest_value,0) AS "loanAmount", t.repayment_time AS "repaymentTime", t.order_status AS "orderStatus", t.collect_tel_remark AS "remark", t.order_id AS "orderId", t.collector_name AS "collectorName", t.payoff_time AS "payoffTime", CASE WHEN t.order_status = 'payoff' THEN DATEDIFF(t.payoff_time,t.repayment_time) WHEN t.order_status = 'payment' THEN DATEDIFF(CURDATE(), t.repayment_time) END AS "overdueDays" FROM `t_task` t LEFT JOIN t_user u ON t.collector_id = u.id
AND t.customer_name LIKE concat('%',#{realname},'%')
AND t.mobile LIKE concat(#{mobile},'%')
AND t.order_id = #{orderId}
AND t.collector_id = #{collectorId}
AND t.action_feedback = #{actionFeedback}
AND t.order_status = #{orderStatus}
AND t.payoff_time BETWEEN #{payoffTimeStart} AND #{payoffTimeEnd}
AND DATEDIFF(NOW(),t.repayment_time) BETWEEN #{overdueDaysStart} AND #{overdueDaysEnd}
AND u.organization_id = #{organizationId}
and t.collect_task_status in ("TASK_IN_PROGRESS","TASK_FINISHED")
